mapper.select:
select * 
from member 
where ${field} = #{value}
and login_type = #{login_type}

select *
from chat_room
where num_of = 2
and num in (
	select a.chat_room_num
	from chat_join a join chat_join b
	on a.chat_room_num = b.chat_room_num
	where a.member_id = #{id1}
	and b.member_id = #{id2}
)

select member_id
from chat_join
where chat_room_num = #{value}

mapper.update:
update chat_room
set num_of = num_of + #{length}
where num = #{roomNum}

update chat_room
set num_of = num_of - 1
where num = #{roomNum}
