mapper.update:
update useditem
set viewcount = viewcount+1
where num = #{num}

update useditem
set subject = #{subject}, content = #{content}, price = #{price}, location = #{location}, imagefolder = #{imagefolder}, 
thumbnail = #{thumbnail}, latitude = #{latitude}, longitude = #{longitude}, deadline = #{deadline}
where num = #{num}

update useditem
set sold = #{sold}
where num = #{num}
